import React, { useEffect, useState } from 'react';
import axios from 'axios';

function LaneList() {
  const [lanes, setLanes] = useState([]);
  const [type, setType] = useState('');
  const [description, setDescription] = useState('');
  const [editing, setEditing] = useState(null);

  const fetchLanes = async () => {
    const res = await axios.get('/api/lanes');
    setLanes(res.data);
  };

  useEffect(() => { fetchLanes(); }, []);

  const handleSave = async () => {
    const data = { type, description };
    if (editing) {
      await axios.put(`/api/lanes/${editing.id}`, data);
    } else {
      await axios.post('/api/lanes', data);
    }
    setType('');
    setDescription('');
    setEditing(null);
    fetchLanes();
  };

  const handleEdit = lane => {
    setEditing(lane);
    setType(lane.type);
    setDescription(lane.description);
  };

  const handleDelete = async (id, type) => {
    if (type === 'IN' || type === 'OUT') {
      alert("Cannot delete default lanes IN or OUT");
      return;
    }
    await axios.delete(`/api/lanes/${id}`);
    fetchLanes();
  };

  return (
    <div className="bg-white p-4 rounded-xl shadow">
      <h2 className="text-xl font-bold mb-4 text-orange-600">Lane Management</h2>

      <div className="flex gap-2 mb-4">
        <input
          className="border p-2 w-1/4"
          placeholder="Type (IN/OUT/BOTH)"
          value={type}
          onChange={e => setType(e.target.value)}
        />
        <input
          className="border p-2 w-1/2"
          placeholder="Description"
          value={description}
          onChange={e => setDescription(e.target.value)}
        />
        <button
          className="bg-orange-500 text-white px-4 py-2 rounded"
          onClick={handleSave}
        >
          {editing ? 'Update' : 'Add'}
        </button>
      </div>

      <table className="w-full text-sm text-left border">
        <thead>
          <tr className="bg-gray-200">
            <th className="p-2">Type</th>
            <th className="p-2">Description</th>
            <th className="p-2">Actions</th>
          </tr>
        </thead>
        <tbody>
          {lanes.map(lane => (
            <tr key={lane.id} className="border-t">
              <td className="p-2">{lane.type}</td>
              <td className="p-2">{lane.description}</td>
              <td className="p-2 flex gap-2">
                <button
                  className="text-blue-600"
                  onClick={() => handleEdit(lane)}
                >Edit</button>
                <button
                  className="text-red-500"
                  onClick={() => handleDelete(lane.id, lane.type)}
                >Delete</button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
}

export default LaneList;
