import React, { useState, useEffect } from 'react';
import './App.css';

function App() {
  const [statistics, setStatistics] = useState({
    totalSlots: 0,
    occupiedSlots: 0,
    availableSlots: 0,
    motorbikes: 0,
    cars: 0,
    vips: 0
  });

  const [entryForm, setEntryForm] = useState({
    licensePlate: '',
    cardNumber: '',
    vehicleType: 'MOTORBIKE'
  });

  const [exitForm, setExitForm] = useState({
    licensePlate: ''
  });

  const [message, setMessage] = useState('');
  const [loading, setLoading] = useState(false);

  // Fetch statistics from backend
  const fetchStatistics = async () => {
    try {
      const response = await fetch('http://localhost:8080/api/parking/statistics');
      if (response.ok) {
        const data = await response.json();
        setStatistics(data);
        setMessage('');
      } else {
        setMessage('Kh√¥ng th·ªÉ k·∫øt n·ªëi v·ªõi server');
      }
    } catch (error) {
      setMessage('L·ªói k·∫øt n·ªëi: ' + error.message);
    }
  };

  // Handle vehicle entry
  const handleVehicleEntry = async (e) => {
    e.preventDefault();
    setLoading(true);

    try {
      const formData = new FormData();
      formData.append('licensePlate', entryForm.licensePlate);
      formData.append('cardNumber', entryForm.cardNumber);
      formData.append('vehicleType', entryForm.vehicleType);
      formData.append('laneInId', '1'); // Default lane

      const response = await fetch('http://localhost:8080/api/parking/checkin', {
        method: 'POST',
        body: formData
      });

      const data = await response.json();

      if (data.success) {
        setMessage(`‚úÖ ${data.message}`);
        setEntryForm({ licensePlate: '', cardNumber: '', vehicleType: 'MOTORBIKE' });
        fetchStatistics(); // Refresh stats
      } else {
        setMessage(`‚ùå ${data.error}`);
      }
    } catch (error) {
      setMessage('‚ùå L·ªói k·∫øt n·ªëi: ' + error.message);
    } finally {
      setLoading(false);
    }
  };

  // Handle vehicle exit
  const handleVehicleExit = async (e) => {
    e.preventDefault();
    setLoading(true);

    try {
      const formData = new FormData();
      formData.append('licensePlate', exitForm.licensePlate);
      formData.append('laneOutId', '2'); // Default lane

      const response = await fetch('http://localhost:8080/api/parking/checkout', {
        method: 'POST',
        body: formData
      });

      const data = await response.json();

      if (data.success) {
        setMessage(`‚úÖ ${data.message}. Ph√≠ ƒë·ªó xe: ${data.fee?.toLocaleString()} VNƒê`);
        setExitForm({ licensePlate: '' });
        fetchStatistics(); // Refresh stats
      } else {
        setMessage(`‚ùå ${data.error}`);
      }
    } catch (error) {
      setMessage('‚ùå L·ªói k·∫øt n·ªëi: ' + error.message);
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchStatistics();
    const interval = setInterval(fetchStatistics, 5000); // Auto refresh every 5s
    return () => clearInterval(interval);
  }, []);

  return (
    <div className="app">
      {/* Header */}
      <header className="app-header">
        <div className="container">
          <h1 className="app-title">üÖøÔ∏è KasickParking</h1>
          <div className="header-status">
            <span className={`status-indicator ${message.includes('‚ùå') ? 'error' : 'success'}`}>
              {message.includes('‚ùå') ? 'üî¥' : 'üü¢'}
            </span>
            <span className="status-text">
              {message || 'H·ªá th·ªëng ho·∫°t ƒë·ªông b√¨nh th∆∞·ªùng'}
            </span>
          </div>
        </div>
      </header>

      <div className="container">
        {/* Main Dashboard */}
        <div className="dashboard">
          {/* Entry Section */}
